mapper.delete:
delete from business_capacity_item
where id = #{id,jdbcType=BIGINT}
and is_checked=false

mapper.insert:

	SELECT LAST_INSERT_ID()

insert into business_capacity_item (company_id, project_id, year,
month, product_value, checked_value,
is_checked,
cst_create, cst_modified, staff_id, staff_name
)
values (#{companyId,jdbcType=BIGINT}, #{projectId,jdbcType=BIGINT}, #{year,jdbcType=INTEGER},
#{month,jdbcType=INTEGER}, #{productValue,jdbcType=DECIMAL}, #{checkedValue,jdbcType=DECIMAL},
#{checked,jdbcType=BIT}, now(), now(), #{staffId,jdbcType=BIGINT}, #{staffName,jdbcType=VARCHAR}
)

mapper.select:
select count(distinct id)
from business_capacity_item where
project_id=#{projectId,jdbcType=BIGINT}
