mapper.delete:

delete from project_team
where project_id = #{projectId,jdbcType=BIGINT}
  and staff_id = #{staffId,jdbcType=BIGINT}

mapper.insert:

insert into project_team (project_id, staff_id, team_role_id
  )
values (#{projectId,jdbcType=BIGINT}, #{staffId,jdbcType=BIGINT}, #{teamRoleId,jdbcType=BIGINT}
  )

mapper.update:

update project_team
set team_role_id = #{teamRoleId,jdbcType=BIGINT}
where project_id = #{projectId,jdbcType=BIGINT}
  and staff_id = #{staffId,jdbcType=BIGINT}

mapper.select:

select project_id, staff_id, team_role_id
from project_team
where project_id = #{projectId,jdbcType=BIGINT}
  and staff_id = #{staffId,jdbcType=BIGINT}

select project_id, staff_id, team_role_id
from project_team
where project_id = #{projectId,jdbcType=BIGINT}
  and team_role_id = #{teamRoleId,jdbcType=BIGINT}

select s.id, s.company_id, s.name, s.sex, s.job_no, s.native_place, s.phone, s.id_card_no, s.address, 
s.entry_date, s.career, s.birthday, s.cst_create, s.cst_modified, s.memo, s.is_del, tr.type as type
from staff s join project_team pt on s.id=pt.staff_id left join team_role tr on tr.id=pt.team_role_id where project_id=#{projectId,jdbcType=BIGINT}

select s.id, s.company_id, s.name, s.sex, s.job_no, s.native_place, s.phone, s.id_card_no, s.address, 
s.entry_date, s.career, s.birthday, s.cst_create, s.cst_modified, s.memo, s.is_del, tr.type as type
from staff s join project_team pt on s.id=pt.staff_id and project_id=#{projectId,jdbcType=BIGINT} and s.is_del=false join team_role tr on tr.id=pt.team_role_id and tr.type=#{type,jdbcType=VARCHAR}
